import PizzaBlock from "./PizzaBlock";
import pizzaDate from "./pizzaDate";
import "./pizzaBlock.scss";
// import cheese from "./img/cheese.png";
const Pizza = () => {
  return (
    <div className="food">
      {pizzaDate.map((obj) => (
        <PizzaBlock pizza={obj} key={obj.id} type={obj.types} />
      ))}
      <div className="adds">
        <div className="adds__title">ДОДАТКИ</div>
        <div className="adds__list">
          <div className="adds__content">
            <div className="adds__img">
              <img className="adds__image" src="./img/cheese.png" alt="" />
            </div>
            <div className="adds__text">Додатковий сир</div>
            <div className="adds__price">
              піца 33 см / 25 грн <br />
              <br />
              піца 45 см / 35 грн
            </div>
            <div className="adds__button">
              <a className="adds__button-link" href="">
                додати
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Pizza;
