// import React, { useState } from "react";
import "./slider.scss";
import dataSlider from "./dataSlider";
import left from "./icon/back.png";
import right from "./icon/next.png";
const Slider = () => {
  //  const [slide, useSlide] = useState([]);

  return (
    <div className="slider">
      <div className="first-slide">
        <img src={dataSlider.map((slid) => slid.source)} alt="" />
      </div>
      <div className="slider__button">
        <div className="slider__button-left">
          <img src={left} alt="" />
        </div>
        <div className="slider__button-right">
          <img src={right} alt="" />
        </div>
      </div>
    </div>
  );
};

export default Slider;
