import { useState } from "react";

const AddsFood = ({ foods }) => {
  const [size, setSize] = useState();
  const sizes = ["33cм", "45см"];
  return (
    <div>
      <div className="adds__img">
        <img className="adds__image" src={foods.src} alt="" />
      </div>
      <div className="adds__text"> {foods.name}</div>
      {sizes.map((obj, i) => (
        <div
          key={i}
          onClick={() => {
            setSize(i);
          }}
          className={size === i ? "active_adds" : ""}
        >
          {sizes[i]}
        </div>
      ))}
      <div className="adds__price"></div>
      <div className="adds__button">
        {/* <a classNameName="adds__button-link" href="">
          додати
        </a> */}
      </div>
    </div>
  );
};

export default AddsFood;
