import "./slider.scss";
// import images from "./dataSlider";

import left from "./icon/back.png";
import right from "./icon/next.png";
import { useState } from "react";

const images = [
  "./sliderImg/1img.jpeg",
  "./sliderImg/4img.jpeg",
  "./sliderImg/2img.jpeg",
];
const Slider = () => {
  const [item, setItem] = useState([0]);
  console.log(item);
  const nextSlid = () => {
    for (let i = 0; i < images.length; i++) {
      setItem(images[i]);
    }
  };
  return (
    <div className="slider">
      <div className="item">
        <img className="slider-img" src={item} alt="" />;
      </div>
      <div className="slider__button">
        <div className="slider__button-left">
          <img onClick={nextSlid} src={left} alt="" />
        </div>
        <div className="slider__button-right">
          <img src={right} alt="" />
        </div>
      </div>
    </div>
  );
};

export default Slider;
