// import React, { useState } from "react";
import "./slider.scss";

import left from "./icon/back.png";
import right from "./icon/next.png";
const Slider = () => {
  //  const [slide, useSlide] = useState([]);

  return (
    <div className="slider">
      <div className="item">
        <div className="first-item">slide 1</div>
        <div className="second-item">slide 2</div>
        <div className="third-item">slide 3</div>
      </div>
      <div className="slider__button">
        <div className="slider__button-left">
          <img src={left} alt="" />
        </div>
        <div className="slider__button-right">
          <img src={right} alt="" />
        </div>
      </div>
    </div>
  );
};

export default Slider;
