import { useState } from "react";
import "./pizzaBlock.scss";

const FoodBlock = ({ pizza }) => {
  const [activeType, setactiveTipe] = useState(0);
  const [activeSize, setactiveSize] = useState(0);
  const types = ["тонке", "грубе"];
  const sizes = ["33cм", "45см"];
  const selectTypes = (index) => {
    setactiveTipe(index);
  };
  const selectSizes = (index) => {
    setactiveSize(index);
  };

  return (
    <div className="food__content">
      <div className="food__img">
        <img className="food-image" src={pizza.src} alt="vvv" />
      </div>
      <div className="food__title">{pizza.name}</div>

      <div className="food__descriptions">{pizza.description}</div>
      <div className="pizza-selector">
        <ul>
          <span className="title"> Тісто </span>
          {types.map((type, index) => (
            <li
              key={type}
              onClick={() => selectTipes(index)}
              className={activeType === index ? "active" : " "}
            >
              {type}
            </li>
          ))}
          {/* <span className="title"> Тісто </span>
          <li>тонке</li>
          <li>грубе</li> */}
        </ul>

        <ul>
          <span className="title">oснова</span>
          <li className="active">томатна</li>
          <li className="disabled">вершкова</li>
        </ul>
        <ul>
          <span className="title">розмір</span>

          {sizes.map((size, index) => (
            <li
              key={size}
              onClick={() => selectSizes(index)}
              className={activeSize === index ? "active" : " "}
            >
              {size}
            </li>
          ))}
        </ul>
      </div>
      <div className="food__price">
        {pizza.price[0]} <br />
        {pizza.price[1]}
      </div>

      <div className="button">
        <button className="button-link">замовити</button>
      </div>
    </div>
  );
};

export default FoodBlock;
