const Slider = () => {
  const [item, setItem] = useState([0]);
  // const nextSlid=()=>{
  //   setItem(item)
  // }
  return (
    <div className="slider">
      <div className="item">
        {images.map((img, i) => {
          // setItem(img);
          console.log(img);
          return <img className="slider-img" src={img} alt="" />;
        })}
      </div>
      <div className="slider__button">
        <div className="slider__button-left">
          <img src={left} alt="" />
        </div>
        <div className="slider__button-right">
          <img src={right} alt="" />
        </div>
      </div>
    </div>
  );
};

export default Slider;
